import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { analyzeWebsite, addMessage, setCurrentUrl } from '../store/slices/chatSlice';

const VisitorClassifier = () => {
  const dispatch = useDispatch();
  const { messages, loading, currentUrl } = useSelector(state => state.chat);
  const [input, setInput] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userInput = input.trim();
    setInput('');

    // Add user message
    dispatch(addMessage({ type: 'user', content: userInput }));

    if (!currentUrl) {
      // First message - expect URL
      try {
        dispatch(setCurrentUrl(userInput));
        const resultAction = await dispatch(analyzeWebsite(userInput));
        
        if (analyzeWebsite.fulfilled.match(resultAction)) {
          console.log('Analysis successful');
        }
      } catch (error) {
        dispatch(addMessage({
          type: 'bot',
          content: "I had trouble analyzing that website. Could you try another one?"
        }));
      }
    } else {
      // Generate response for user's option selection
      try {
        const response = await fetch('http://localhost:5000/api/respond', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            option: userInput,
            context: currentUrl
          })
        });
        
        if (response.ok) {
          const data = await response.json();
          dispatch(addMessage({
            type: 'bot',
            content: data.response
          }));
        }
      } catch (error) {
        console.error('Error generating response:', error);
      }
    }
  };

  const handleOptionClick = async (option) => {
    setInput('');
    dispatch(addMessage({ type: 'user', content: option }));

    try {
      const response = await fetch('http://localhost:5000/api/respond', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          option: option,
          context: currentUrl
        })
      });
      
      if (response.ok) {
        const data = await response.json();
        dispatch(addMessage({
          type: 'bot',
          content: data.response
        }));
      }
    } catch (error) {
      console.error('Error generating response:', error);
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-4">
      <div className="bg-white rounded-lg shadow-lg">
        <div className="h-[500px] overflow-y-auto p-4">
          {messages.map((message, index) => (
            <div
              key={index}
              className={`mb-4 ${message.type === 'user' ? 'text-right' : 'text-left'}`}
            >
              <div
                className={`inline-block p-3 rounded-lg ${
                  message.type === 'user'
                    ? 'bg-blue-500 text-white'
                    : 'bg-gray-100 text-gray-800'
                }`}
              >
                <p>{message.content}</p>
                {message.options && (
                  <div className="mt-2 space-y-2">
                    {message.options.map((option, idx) => (
                      <button
                        key={idx}
                        onClick={() => handleOptionClick(option)}
                        className="block w-full text-left px-3 py-2 rounded bg-white hover:bg-gray-50 border border-gray-200"
                      >
                        {option}
                      </button>
                    ))}
                  </div>
                )}
              </div>
            </div>
          ))}
          {loading && (
            <div className="flex items-center justify-center">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
            </div>
          )}
        </div>
        
        <div className="border-t p-4">
          <form onSubmit={handleSubmit} className="flex gap-2">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder={currentUrl ? "Type your message..." : "Enter a website URL (e.g., apple.com)"}
              className="flex-1 px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <button
              type="submit"
              disabled={loading || !input.trim()}
              className="px-4 py-2 bg-blue-500 text-white rounded disabled:bg-blue-300 hover:bg-blue-600 transition-colors"
            >
              Send
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default VisitorClassifier;
