import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { analyzeWebsite, addMessage, setCurrentUrl } from '../store/slices/chatSlice';

const VisitorClassifier = () => {
  const dispatch = useDispatch();
  const { messages, loading } = useSelector(state => state.chat);
  const [input, setInput] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    setError('');
    
    // Clean the URL
    let url = input.trim().toLowerCase();
    console.log('Processing URL:', url);

    try {
      // Add message to show what URL is being analyzed
      dispatch(addMessage({ 
        type: 'user', 
        content: `Analyzing website: ${url}` 
      }));

      // Set the current URL
      dispatch(setCurrentUrl(url));

      // Dispatch the analyze action
      const resultAction = await dispatch(analyzeWebsite(url));
      
      if (analyzeWebsite.fulfilled.match(resultAction)) {
        console.log('Analysis successful:', resultAction.payload);
      } else {
        console.error('Analysis failed:', resultAction.error);
        setError(resultAction.error?.message || 'Failed to analyze website');
      }
    } catch (err) {
      console.error('Error in handleSubmit:', err);
      setError(err.message || 'An error occurred while analyzing the website');
    }

    setInput('');
  };

  const handleOptionClick = (option) => {
    dispatch(addMessage({ 
      type: 'user', 
      content: `Selected: ${option}` 
    }));
    dispatch(addMessage({
      type: 'bot',
      content: `Great choice! What specific information about ${option} are you looking for?`
    }));
  };

  return (
    <div className="max-w-2xl mx-auto p-4">
      <div className="bg-white rounded-lg shadow-lg">
        <div className="h-[500px] overflow-y-auto p-4">
          {/* Messages */}
          {messages.map((message, index) => (
            <div
              key={index}
              className={`mb-4 ${message.type === 'user' ? 'text-right' : 'text-left'}`}
            >
              <div
                className={`inline-block p-3 rounded-lg ${
                  message.type === 'user'
                    ? 'bg-blue-500 text-white'
                    : 'bg-gray-100 text-gray-800'
                }`}
              >
                <p>{message.content}</p>
                {message.options && (
                  <div className="mt-2 space-y-2">
                    {message.options.map((option, idx) => (
                      <button
                        key={idx}
                        onClick={() => handleOptionClick(option)}
                        className="block w-full text-left px-3 py-2 rounded bg-white hover:bg-gray-50 border border-gray-200"
                      >
                        {option}
                      </button>
                    ))}
                  </div>
                )}
              </div>
            </div>
          ))}
          
          {/* Loading indicator */}
          {loading && (
            <div className="text-center p-4">
              <div className="inline-block animate-spin text-blue-500">⚡</div>
              <p className="text-sm text-gray-500">Analyzing website...</p>
            </div>
          )}
        </div>

        {/* Input form */}
        <div className="border-t p-4">
          <form onSubmit={handleSubmit} className="space-y-2">
            <div className="flex gap-2">
              <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Enter a website URL (e.g., apple.com)"
                className="flex-1 px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <button
                type="submit"
                disabled={loading || !input.trim()}
                className="px-4 py-2 bg-blue-500 text-white rounded disabled:bg-blue-300 hover:bg-blue-600 transition-colors"
              >
                {loading ? 'Analyzing...' : 'Analyze'}
              </button>
            </div>
            {error && (
              <div className="text-red-500 text-sm px-2">
                {error}
              </div>
            )}
          </form>
        </div>
      </div>
    </div>
  );
};

export default VisitorClassifier;
