import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { analyzeWebsite } from './store/websiteSlice';

function App() {
  const [url, setUrl] = useState('');
  const dispatch = useDispatch();
  const { result, loading, error } = useSelector((state) => state.website);

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(analyzeWebsite(url));
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
      <div className="w-full max-w-md bg-white shadow-md rounded-lg p-6">
        <h1 className="text-2xl font-bold mb-4 text-center">
          Website Intent Classifier
        </h1>

        <form onSubmit={handleSubmit} className="space-y-4">
          <input 
            type="url" 
            placeholder="Enter website URL (e.g., apple.com)" 
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            required
            className="w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />

          <button 
            type="submit" 
            disabled={loading}
            className="w-full bg-blue-500 text-white py-2 rounded-md hover:bg-blue-600 transition duration-300"
          >
            {loading ? 'Analyzing...' : 'Analyze Website'}
          </button>
        </form>

        {error && (
          <div className="mt-4 text-red-500 text-center">
            {error}
          </div>
        )}

        {result && (
          <div className="mt-4 bg-gray-50 p-4 rounded-md">
            <h2 className="font-bold mb-2">{result.question}</h2>
            {result.options.map((option, index) => (
              <div 
                key={index} 
                className="p-2 border rounded mb-2 hover:bg-white cursor-pointer"
              >
                {option}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
