// src/components/VisitorClassifier.js
import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { analyzeWebsite, addMessage, setCurrentUrl, setSelectedOption } from '../store/slices/chatSlice';

const VisitorClassifier = () => {
  const dispatch = useDispatch();
  const { messages, loading } = useSelector(state => state.chat);
  const [input, setInput] = useState('');
  const [error, setError] = useState('');

  const cleanUrl = (url) => {
    // Remove leading/trailing whitespace
    let cleaned = url.trim();
    
    // Remove protocol if present
    cleaned = cleaned.replace(/^(https?:\/\/)/, '');
    
    // Remove www. if present
    cleaned = cleaned.replace(/^www\./, '');
    
    return cleaned;
  };

  const isValidUrl = (string) => {
    // Basic URL format validation
    const pattern = /^(https?:\/\/)?([\da-z\.-]+)\.([a-z\.]{2,6})([\/\w \.-]*)*\/?$/;
    return pattern.test(string);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    
    if (!input.trim()) return;

    const cleanedUrl = cleanUrl(input);
    if (!isValidUrl(cleanedUrl)) {
      setError('Please enter a valid website URL (e.g., apple.com)');
      return;
    }

    dispatch(addMessage({ type: 'user', content: cleanedUrl }));
    dispatch(setCurrentUrl(cleanedUrl));
    
    try {
      await dispatch(analyzeWebsite(cleanedUrl)).unwrap();
    } catch (err) {
      setError(err.message || 'Failed to analyze website. Please try a different URL.');
    }
    
    setInput('');
  };

  const handleOptionClick = (option) => {
    dispatch(addMessage({ type: 'user', content: option }));
    dispatch(setSelectedOption(option));
    dispatch(addMessage({
      type: 'bot',
      content: `Great choice! What specific features or information about ${option} are you most interested in?`
    }));
  };

  return (
    <div className="max-w-2xl mx-auto p-4">
      <div className="bg-white rounded-lg shadow-lg">
        <div className="h-[500px] overflow-y-auto p-4">
          {messages.map((message, index) => (
            <div
              key={index}
              className={`mb-4 ${message.type === 'user' ? 'text-right' : 'text-left'}`}
            >
              <div
                className={`inline-block p-3 rounded-lg ${
                  message.type === 'user'
                    ? 'bg-blue-500 text-white'
                    : 'bg-gray-100 text-gray-800'
                }`}
              >
                <p>{message.content}</p>
                {message.options && (
                  <div className="mt-2 space-y-2">
                    {message.options.map((option, idx) => (
                      <button
                        key={idx}
                        onClick={() => handleOptionClick(option)}
                        className="block w-full text-left px-3 py-2 rounded bg-white hover:bg-gray-50 border border-gray-200"
                      >
                        {option}
                      </button>
                    ))}
                  </div>
                )}
              </div>
            </div>
          ))}
          {loading && (
            <div className="text-center">
              <div className="inline-block animate-spin text-blue-500">âŒ›</div>
            </div>
          )}
        </div>
        <div className="border-t p-4">
          <form onSubmit={handleSubmit} className="space-y-2">
            <div className="flex gap-2">
              <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Enter a website URL (e.g., apple.com)"
                className="flex-1 px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <button
                type="submit"
                disabled={loading || !input.trim()}
                className="px-4 py-2 bg-blue-500 text-white rounded disabled:bg-blue-300 hover:bg-blue-600 transition-colors"
              >
                {loading ? 'Analyzing...' : 'Analyze'}
              </button>
            </div>
            {error && (
              <div className="text-red-500 text-sm">
                {error}
              </div>
            )}
          </form>
        </div>
      </div>
    </div>
  );
};

export default VisitorClassifier;
