import React, { useState, useEffect, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { analyzeWebsite, addMessage, setCurrentUrl, resetChat } from '../store/slices/chatSlice';
import { AlertCircle, Send, RefreshCw, Bot, User } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { toast } from 'react-hot-toast';

const VisitorClassifier = () => {
  const dispatch = useDispatch();
  const { messages, loading, currentUrl } = useSelector(state => state.chat);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);
  const [showResetConfirm, setShowResetConfirm] = useState(false);

  // Auto scroll to bottom when new messages arrive
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleStartNewChat = () => {
    setShowResetConfirm(true);
  };

  const confirmReset = () => {
    dispatch(resetChat());
    setInput('');
    setShowResetConfirm(false);
    toast.success('Started a new chat!', {
      icon: '🎉',
      style: {
        borderRadius: '10px',
        background: '#333',
        color: '#fff',
      },
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userInput = input.trim();
    setInput('');

    // Show notification for user message
    toast.success('Message sent!', {
      icon: '✉️',
      duration: 2000,
    });

    if (!currentUrl) {
      const cleanedUrl = userInput.toLowerCase().replace(/^(https?:\/\/)?(www\.)?/, '');
      dispatch(addMessage({ type: 'user', content: cleanedUrl }));
      
      try {
        const resultAction = await dispatch(analyzeWebsite(cleanedUrl));
        if (!resultAction.error) {
          dispatch(setCurrentUrl(cleanedUrl));
          toast.success('Website analyzed successfully!', {
            icon: '🔍',
            duration: 3000,
          });
        } else {
          toast.error('Failed to analyze website', {
            icon: '❌',
          });
        }
      } catch (error) {
        console.error('Analysis error:', error);
      }
    } else {
      dispatch(addMessage({ type: 'user', content: userInput }));
      try {
        const response = await fetch('http://localhost:5000/api/respond', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            option: userInput,
            context: currentUrl
          })
        });
        
        if (response.ok) {
          const data = await response.json();
          dispatch(addMessage({
            type: 'bot',
            content: data.response
          }));
        }
      } catch (error) {
        console.error('Response error:', error);
        toast.error('Failed to get response');
      }
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-4">
      <motion.div 
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-white rounded-lg shadow-lg"
      >
        {/* Header */}
        <div className="border-b p-4 flex justify-between items-center bg-gradient-to-r from-blue-500 to-purple-500 text-white rounded-t-lg">
          <div className="flex items-center space-x-2">
            <Bot className="h-6 w-6" />
            <h2 className="text-lg font-semibold">AI Website Analyzer</h2>
          </div>
          <button
            onClick={handleStartNewChat}
            className="flex items-center space-x-1 px-3 py-1 bg-white/20 rounded hover:bg-white/30 transition-colors"
          >
            <RefreshCw className="h-4 w-4" />
            <span>New Chat</span>
          </button>
        </div>

        {/* Messages Area */}
        <div className="h-[500px] overflow-y-auto p-4 bg-gray-50">
          <AnimatePresence>
            {messages.map((message, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, x: message.type === 'user' ? 20 : -20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0 }}
                transition={{ duration: 0.3 }}
                className={`mb-4 flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
              >
                <div
                  className={`flex items-start space-x-2 max-w-[80%] ${
                    message.type === 'user' ? 'flex-row-reverse' : 'flex-row'
                  }`}
                >
                  <div className={`p-2 rounded-full ${
                    message.type === 'user' ? 'bg-blue-500' : 'bg-gray-200'
                  }`}>
                    {message.type === 'user' ? 
                      <User className="h-4 w-4 text-white" /> : 
                      <Bot className="h-4 w-4 text-gray-600" />
                    }
                  </div>
                  <div
                    className={`p-3 rounded-lg shadow-sm ${
                      message.type === 'user'
                        ? 'bg-blue-500 text-white'
                        : 'bg-white text-gray-800'
                    }`}
                  >
                    <p>{message.content}</p>
                    {message.options && (
                      <motion.div
                        initial={{ opacity: 0, y: 10 }}
                        animate={{ opacity: 1, y: 0 }}
                        className="mt-2 space-y-2"
                      >
                        {message.options.map((option, idx) => (
                          <motion.button
                            key={idx}
                            whileHover={{ scale: 1.02 }}
                            whileTap={{ scale: 0.98 }}
                            onClick={() => handleOptionClick(option)}
                            className="block w-full text-left px-3 py-2 rounded bg-white hover:bg-gray-50 border border-gray-200 transition-colors"
                          >
                            {option}
                          </motion.button>
                        ))}
                      </motion.div>
                    )}
                  </div>
                </div>
              </motion.div>
            ))}
          </AnimatePresence>
          {loading && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="flex justify-center"
            >
              <div className="typing-indicator">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </motion.div>
          )}
          <div ref={messagesEndRef} />
        </div>

        {/* Input Area */}
        <div className="border-t p-4 bg-white rounded-b-lg">
          <form onSubmit={handleSubmit} className="flex gap-2">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder={currentUrl ? "Type your message..." : "Enter a website URL (e.g., apple.com)"}
              className="flex-1 px-4 py-2 border rounded-full focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              type="submit"
              disabled={loading || !input.trim()}
              className="px-6 py-2 bg-blue-500 text-white rounded-full disabled:bg-blue-300 hover:bg-blue-600 transition-colors flex items-center space-x-2"
            >
              <Send className="h-4 w-4" />
              <span>Send</span>
            </motion.button>
          </form>
        </div>

        {/* Reset Confirmation Modal */}
        {showResetConfirm && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
            <motion.div
              initial={{ scale: 0.9, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              className="bg-white p-6 rounded-lg shadow-xl"
            >
              <h3 className="text-lg font-semibold mb-4">Start New Chat?</h3>
              <p className="text-gray-600 mb-4">This will clear the current conversation.</p>
              <div className="flex justify-end space-x-3">
                <button
                  onClick={() => setShowResetConfirm(false)}
                  className="px-4 py-2 text-gray-600 hover:bg-gray-100 rounded"
                >
                  Cancel
                </button>
                <button
                  onClick={confirmReset}
                  className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
                >
                  Confirm
                </button>
              </div>
            </motion.div>
          </div>
        )}
      </motion.div>
    </div>
  );
};

export default VisitorClassifier;
