// src/components/VisitorClassifier.js
import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { analyzeWebsite, addMessage, setCurrentUrl, setSelectedOption } from '../store/slices/chatSlice';

const VisitorClassifier = () => {
  const dispatch = useDispatch();
  const { messages, loading } = useSelector(state => state.chat);
  const [input, setInput] = useState('');

  const isValidUrl = (string) => {
    try {
      new URL(string.startsWith('http') ? string : `https://${string}`);
      return true;
    } catch (_) {
      return false;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    dispatch(addMessage({ type: 'user', content: input }));

    if (isValidUrl(input)) {
      dispatch(setCurrentUrl(input));
      dispatch(analyzeWebsite(input));
    } else {
      dispatch(addMessage({
        type: 'bot',
        content: "That doesn't look like a valid URL. Could you please share a website URL? For example: apple.com"
      }));
    }
    
    setInput('');
  };

  const handleOptionClick = (option) => {
    dispatch(addMessage({ type: 'user', content: option }));
    dispatch(setSelectedOption(option));
    dispatch(addMessage({
      type: 'bot',
      content: `Great choice! What specific features or information about ${option} are you most interested in?`
    }));
  };

  return (
    <div className="max-w-2xl mx-auto p-4">
      <div className="bg-white rounded-lg shadow-lg">
        <div className="h-[500px] overflow-y-auto p-4">
          {messages.map((message, index) => (
            <div
              key={index}
              className={`mb-4 ${message.type === 'user' ? 'text-right' : 'text-left'}`}
            >
              <div
                className={`inline-block p-3 rounded-lg ${
                  message.type === 'user'
                    ? 'bg-blue-500 text-white'
                    : 'bg-gray-100 text-gray-800'
                }`}
              >
                <p>{message.content}</p>
                {message.options && (
                  <div className="mt-2 space-y-2">
                    {message.options.map((option, idx) => (
                      <button
                        key={idx}
                        onClick={() => handleOptionClick(option)}
                        className="block w-full text-left px-3 py-2 rounded bg-white hover:bg-gray-50 border border-gray-200"
                      >
                        {option}
                      </button>
                    ))}
                  </div>
                )}
              </div>
            </div>
          ))}
          {loading && (
            <div className="text-center">
              <span className="inline-block animate-spin">âŒ›</span>
            </div>
          )}
        </div>
        <div className="border-t p-4">
          <form onSubmit={handleSubmit} className="flex gap-2">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Enter a website URL..."
              className="flex-1 px-3 py-2 border rounded"
            />
            <button
              type="submit"
              disabled={loading || !input.trim()}
              className="px-4 py-2 bg-blue-500 text-white rounded disabled:bg-blue-300"
            >
              Send
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default VisitorClassifier;
